$(document).ready(function(){
    $("#login-but").click(function(){

        $("#box1").css("display","block")
        
    });

    $("#box1").mouseenter(function(){

        $("#box1").css("display","block")
        
    });
    
    
    $("#login-but").mouseleave(function(){
        
        $("#box1").css("display","none")  

    });

    $("#box1").mouseleave(function(){
        
        $("#box1").css("display","none")  

    });
});